<button title="Update Log Flags" onclick="changeLogsStatus(event)" type="button" id ="{{adminDashPanel.countItems.logs}}" class="logstatus btn btn-small btn-primary">Turn {{adminDashPanel.countItems.logs}} Logs</button>
<button title="Clear Logs" onclick="clearLogs(event)" type="button" class="clearLogs btn btn-small btn-danger">Clear Logs</button>
