<?php    require_once 'NamedConstants.php';    require_once 'EntityManager.php';    /**    *     * @Author Sneidon Dumela <sneidon@yahoo.com>    * @version 1.0    */    class AudioConstructsDAO {        private $aEntityManager;        public function AudioConstructsDAO() {            $this->aEntityManager = new EntityManager(NULL);        }        /**        *         * @return Array with status and message        */        public function getAllAudioConstructs() {            $aNameQuery = new NamedQuery(NamedConstants::$LIST_ALL_AUDIO_CONSTRUCTS);                        $aResult =  $this->aEntityManager->queryResults($aNameQuery->getQuery());            $aCount = $this->aEntityManager->getSql()->getResultCount($aResult['resultsArray']);                        if($aCount == 0){                 return array(status=> false, message=>"No active audio constructs found on system.");            }else{                $aRecords = $this->aEntityManager->getSql()->getRecordsInResults($aResult['resultsArray']);                return array(status=> true,resultsArray => $aRecords );            }        }    }?>